	public static int backgroundEndX(int camX)
	{
		int endX = 0;
		if (camX < 3200 - 800)
		{
			endX = (camX / 32) + (800 / 32) + 1;
		}
		else
		{
			endX = backgroundTiles[0].length;
		}
		return endX;
	}
	
	public static int backgroundEndY(int camY)
	{
		int endY = 0;
		if (camY < 2400 - 600)
		{
			endY = (camY / 32) + (600 / 32) + 2;
		}
		else
		{
			endY = backgroundTiles.length;
		}
		return endY;
	}
